import shortid from 'shortid';

const initialState = {
  columns: []
};

const reducer = (state, action) => {
    switch(action.type) {
      case 'ADD_COLUMN':
        return { ...state, columns: [...state.columns, { ...action.payload, id: shortid() }]};
      default:
        return state;
    }
  };

export default ColumnsReduser;
