import shortid from 'shortid';

const initialState = {
  columns: []
};

const ColumnsReduser = (state = initialState, action) => {
    switch (action.type) {
        case 'ADD_COLUMN':
            return [
                ...state,
                {
                  id: shortid.generate(),
                  title: action.payload.title,
                },
              ];
            default:
              return state;
          }
    };

export default ColumnsReduser;
