import shortid from 'shortid';

const CardReduser = (state = [], action) => {
  switch (action.type) {
    case 'ADD_CARD':
      return [
        ...state,
        {
          id: shortid.generate(),
          title: action.payload.title,
        },
      ];
    default:
      return state;
  }
};

export default CardReduser;
