import shortid from 'shortid';

const ColumnsReducer = (state = [], action) => {
    switch(action.type) {
      case 'ADD_COLUMN':
        return { ...state, columns: [...state.columns, { ...action.payload, id: shortid() }]};
      default:
        return state;
    }
  }

  export default ColumnsReducer;
