import styles from './Column.module.scss';
import Card from '../Card/Card';
import CardForm from '../CardForm/CardForm';
import { useSelector } from 'react-redux';

const Column = (props) => {
  const cards = useSelector((state) => {
    // Filter cards based on columnId
    const columnCards = state.cards.filter(
      (card) => card.columnId === props.id
    );

    // If searchString exists, filter cards based on it
    if (state.searchString) {
      const searchString = state.searchString.toLowerCase();
      return columnCards.filter(
        (card) => card.title.toLowerCase().indexOf(searchString) !== -1
      );
    } else {
      return columnCards;
    }
  });

  return (
    <article className={styles.column}>
      <h2 className={styles.title}>
        <span className={styles.icon + ' fa fa-' + props.icon}></span>
        {props.title}
      </h2>
      <ul className={styles.cards}>
        {cards.map((card) => (
          <Card key={card.id} title={card.title} />
        ))}
      </ul>
      <CardForm columnId={props.id} />
    </article>
  );
};

export default Column;
