import { ADD_COLUMN } from '../actions/types';
import shortid from 'shortid';

const initialState = {
  columns: []
};

const columnsReducer = (state = initialState, action) => {
  switch(action.type) {
    case ADD_COLUMN:
      return { ...state, columns: [...state.columns, { ...action.payload, id: shortid() }]};
    default:
      return state;
  }
}

export default ColumsReducer;
