import shortid from 'shortid';

const initialState = {
  columns: []
};

const columnsReducer = (state = initialState, action) => {
  switch(action.type) {
    case 'ADD_COLUMN':
      const newColumn = {
        id: shortid.generate(),
        title: action.payload.title,
        cards: []
      };
      return {
        ...state,
        columns: [...state.columns, newColumn]
      };
    default:
      return state;
  }
};

export default columnsReducer;
